    /// <summary>
    /// 
    /// </summary>
    /// <param name="icon"></param>
    /// <param name="fileExtension"></param>
    /// <param name="fileName"></param>
    /// <param name="fileDescription"></param>
    public static void UpdateAssociation(Icon icon, string fileExtension, string fileName, string fileDescription)
    {
      // save the icon to the appdirectory

      string appPath = Application.ExecutablePath;
      string appName = Path.GetFileNameWithoutExtension(appPath);

      string appData = Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData);
      string appDirectory = Path.Combine(appData, "MAPA Inc", appName);

      if (!Directory.Exists(appDirectory))
      {
        Directory.CreateDirectory(appDirectory);
      }

      string appIcon = Path.Combine(appDirectory, "file.ico");
      using (Stream stream = File.Create(appIcon))
      {
        icon.Save(stream);
      }

      // update the association

      UpdateAssociation(new AppAssociationArg
      {
        AppPath = appPath,
        Create = true,
        Extension = fileExtension.ToLower(),
        FileDescription = fileDescription,
        FileName = fileName,
        IconPath = appIcon,
        Name = appName,
      });
    }

    /// <summary>
    /// 
    /// </summary>
    /// <param name="value"></param>
    public static void UpdateAssociation(AppAssociationArg value)
    {
      string appData = Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData);

      string directory = Path.Combine(appData, @"MAPA Inc\References\Assemblies");
      if (!Directory.Exists(directory))
      {
        Directory.CreateDirectory(directory);
      }

      string filepath = Path.Combine(directory, "associate.exe");
      if (File.Exists(filepath))
      {
        File.Delete(filepath);
      }

      var csc = new CSharpCodeProvider(new Dictionary<string, string>() { { "CompilerVersion", "v3.5" } });
      var parameters = new CompilerParameters(new[] { "mscorlib.dll", "System.Core.dll" }, filepath, true);
      parameters.GenerateExecutable = true;
    
      // compile from source
      var results = csc.CompileAssemblyFromSource(parameters, Encoding.ASCII.GetString(source));
      if (results.Errors.Count > 0)
      {
        throw new Exception(results.Errors[0].ToString());
      }

      // retrieve the bytes that we're sending
      byte[] buffer = null;
      using (var stream = new MemoryStream())
      {
        var writer = new BinaryWriter(stream);
        value.Write(writer);
        buffer = stream.GetBuffer();
      }

      // start the process
      try
      {
        ProcessStartInfo info = new ProcessStartInfo(filepath, Convert.ToBase64String(buffer));
        info.Verb = "runas";
        info.UseShellExecute = true;
        info.WindowStyle = ProcessWindowStyle.Hidden;
        Process.Start(info).WaitForExit(30000);
      }
      catch (Exception ex)
      {
        File.Delete(filepath);
        throw ex;
      }
    }
